import axios from 'axios';
import { Link } from 'react-router-dom';
import Adva from './Adva';
import Advaimg from './Advaimg';

function Events() {
    const [events, setEvents] = useState([]);
    const [info,setInfo]=useState(null)
    const [addnewevent,setAddnewevent]=useState(null)

    useEffect(() => {
      const fetchEvents = async () => {
        try {
          const res = await axios.get('http://localhost:4000/events');
          setEvents(res.data);
          console.log(res.data);
        } catch (error) {
          console.error('Error fetching events:', error);
        }
      };
  
      fetchEvents();
    }, []);
    const handleinfo=(e)=>{
       let info=events.filter((m)=>{
        return m.title==e.target
       })

    }
    return (
    <div style={{display:"flex",justifyContent:"space-between"}}>
        <div className="listofevents">
            <h4>events list</h4>
            {
                events && events.map((m)=>(
                    <ul>
                        <li onClick={()=>{handleinfo(e)}} >{m.title}</li>
                    </ul>
                ))
            }
            <button onClick={()=>{
              setAddnewevent(1);
            }} >Add new Event</button>

        </div>
        <div className="information">
          { addnewevent && <Adva/>

          }
          {
            // info && <Advaimg title={}
          }

        </div>
    </div>
  )
}

export default Events
