import React, { useRef, useState } from 'react'

function Allevents() {
    let title=useRef()
    let discription=useRef()
    let images=useRef()
    let [eventupdate,setEventupdate]=useState(null)
    let [newimage,setNewimage]=useState([])
    const addimage=(e)=>{

      newimage=newimage.push(images)
      setNewimage(newimage)
   }
    const handleSubmit=(e)=>{
      e.preventDefault();
      let  newEvent={
            title:title.current.value,
            discription:discription.current.value,
            images:newimage,
        }
       
        fetch("http://localhost:4000/events",{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify(newEvent)})
        .then(
           ()=>{
            setEventupdate("done added")
            console.log("hello");
           }
            
        ).catch(
            setEventupdate("Not able to add event try agin")
        )
    }
  return (
    <div className="addevents">
      <h4>Add new event here</h4>
    <form onSubmit={handleSubmit}>
        <input type="text" placeholder='Enter title' ref={title}/>
        <input type="text" placeholder='Enter discription' ref={discription}/>
        <input type="file" placeholder="select image" ref={images}/> <button onClick={addimage} >add new image</button>
        <input type="submit" /> 
        { eventupdate &&  <h2 style={{color:'green'}} >{eventupdate}</h2> }
        
    </form>
    
</div>
  )
}

export default Allevents
