import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Adva from './Adva';
import Advaimg from './Advaimg';

function Events() {
  const [events, setEvents] = useState([]);
  const [info, setInfo] = useState(null);
  const [addNewEvent, setAddNewEvent] = useState(false);

  useEffect(() => {
    const fetchEvents = async () => {
      try {
        const res = await axios.get('http://localhost:4000/events');
        setEvents(res.data);
        console.log(res.data);
      } catch (error) {
        console.error('Error fetching events:', error);
      }
    };

    fetchEvents();
  }, []);

  function handleInfo(title) {
    const info = events.filter((event) => event.title === title);
    setInfo(info);
    console.log(info);
  }

  return (
    <div style={{ display: 'flex', justifyContent: 'space-between' }}>
      <div className="listofevents">
        <h4>Events List</h4>
        {events.map((event) => (
          <ul key={event.eventId}>
            <li onClick={() => handleInfo(event.title)}>{event.title}</li>
          </ul>
        ))}
        <button onClick={() => setAddNewEvent(true)}>Add New Event</button>
      </div>
      <div className="information">
        {addNewEvent ? <Adva /> : null}
        {info &&
          info.map((event) => (
            <div key={event.eventId}>
              <h2>{event.title}</h2>
              <p>{event.description}</p>
              <Advaimg event={event} />
            </div>
          ))}
      </div>
    </div>
  );
}

export default Events;
