import React, { useRef, useState } from 'react'

function Allevents() {
    let title=useRef
    let discription=useRef()
    let images=useRef()
    let [eventupdate,setEventupdate]=useState(null)
    const handleSubmit=(e)=>{
      e.preventD
      let  newEvent={
            title:title.current.value,
            discription:discription.current.value,
            images:images.current.value,
        }
        fetch("http://localhost:4000/events",{method:"POST",headers:{"Content-Type":"application.json"},body:JSON.stringify(newEvent)})
        .then(
            setEventupdate("New event added sucessfully")
        ).catch(
            setEventupdate("Not able to add event try agin")
        )
    }
  return (
    <div className="addevents">
    <form onSubmit={handleSubmit}>
        <input type="text" placeholder='Enter title' ref={title}/>
        <input type="text" placeholder='Enter discription' ref={discription}/>
        <input type="text" placeholder='add images' ref={images}/>
        <input type="submit" /> 
        { null &&  <h1>{eventupdate}</h1> }
        
    </form>
    
</div>
  )
}

export default Allevents
