import React, { useState } from 'react';
import axios from 'axios';

const Registration = () => {
  const [formData, setFormData] = useState({});
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleFileChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.files[0] });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    const data = new FormData();
    for (let key in formData) {
      data.append(key, formData[key]);
    }

    try {
      const response = await axios.post('/api/registration', data);
      console.log(response.data); // Data saved successfully
      setFormData({});
    } catch (error) {
      console.error(error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <h1>Registration Form</h1>
      <form onSubmit={handleSubmit}>
        <label>
          Username:
          <input type="text" name="username" onChange={handleChange} required />
        </label>
        <br />
        <label>
          Phone No:
          <input type="tel" name="phoneNo" onChange={handleChange} required />
        </label>
        <br />
        <label>
          Email:
          <input type="email" name="email" onChange={handleChange} required />
        </label>
        <br />
        <label>
          Password:
          <input type="password" name="password" onChange={handleChange} required />
        </label>
        <br />
        <label>
          Confirm Password:
          <input type="password" name="confirmPassword" onChange={handleChange} required />
        </label>
        <br />
        <label>
          Personal Address:
          <input type="text" name="personalAddress" onChange={handleChange} required />
        </label>
        <br />
        <label>
          State:
          <input type="text" name="state" onChange={handleChange} required />
        </label>
        <br />
        <label>
          Country:
          <input type="text" name="country" onChange={handleChange} required />
        </label>
        <br />
        <label>
          Zipcode:
          <input type="text" name="zipcode" onChange={handleChange} required />
        </label>
        <br />
        <label>
          Company Name:
          <input type="text" name="companyName" onChange={handleChange} required />
        </label>
        <br />
        <label>
          Company Contact No:
          <input type="tel" name="companyContactNo" onChange={handleChange} required />
        </label>
        <br />
        <label>
          Company Address:
          <input type="text" name="companyAddress" onChange={handleChange} required />
        </label>
        <br />
        <label>
          Company State:
          <input type="text" name="companyState" onChange={handleChange} required />
        </label>
        <br />
        <label>
          Company Country:
          <input type="text" name="companyCountry" onChange={handleChange} required />
        </label>
        <br />
        <label>
          Company Zipcode:
          <input type="text" name="companyZipcode" onChange={handleChange} required />
        </label>
        <br />
        <label>
          GSTN:
          <input type="text" name="gstn" onChange={handleChange} required />
        </label>
        <br />
        <label>
          Business Location:
          <input type="text" name="businessLocation" onChange={handleChange} required />
        </label>
        <br />
        <label>
          Business Category:
          <input type="text" name="businessCategory" onChange={handleChange} required />
        </label>
        <br />
        <label>
          Business Type:
          <input type="text" name="businessType" onChange={handleChange} required />
        </label>
        <br />
        <label>
          Business Description:
          <textarea name="businessDescription" onChange={handleChange} required />
        </label>
        <br />
        <label>
          Upload Image:
          <input type="file" name="image" onChange={handleFileChange} required />
        </label>
        <br />
        <label>
          Upload Video:
          <input type="file" name="video" onChange={handleFileChange} required />
        </label>
        <br />
        <button type="submit" disabled={loading}>
          {loading ? 'Loading...' : 'Register'}
        </button>
      </form>
    </div>
  );
};

export default Registration;
