    setSelectedFiles(files);
    selectedFiles.forEach((file) => {
      let tem
      let imgs=imgs.append('images', file);
      console.log(imgs);
      setimgs(imgs)

    });
  };
  const onFinish = async (e) => {
    e.preventDefault();
    setLoading(true);

     
    const formData = {
      userName: userName.current.value,
      phoneNo: phoneNo.current.value,
      email: email.current.value,
      password: password.current.value,
      confirmPassword: confirmPassword.current.value,
      personalAddress: personalAddress.current.value,
      state: state.current.value,
      country: country.current.value,
      city:city.current.value,
      zipcode: zipcode.current.value,
      companyName: companyName.current.value,
      companyContactNo: companyContactNo.current.value,
      companyAddress: companyAddress.current.value,
      companyState: companyState.current.value,
      companyCountry: companyCountry.current.value,
      companyCity:companyCity.current.value,
      companyZipcode: companyState.current.value,
      gstn: gstn.current.value,
      businessLocation: businessLocation.current.value,
      headbranch:headbranch.current.value,
      subbranch1:subbranch1.current.value,
      subbranch2:subbranch2.current.value,
      businessCategory: businessCategory.current.value,
      businessType: businessType.current.value,
      plan: plan.current.value,
      businessDescription: businessDescription.current.value,
      image: imgs,
      video: video.current.value
    }
    console.log(formData);

    try {
      const response = await axios.post('http://localhost:4000/registration', formData);
      console.log(response.data); // Data saved successfully
      // Reset the form here if needed
    } catch (error) {
      console.error(error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container mt-5 w-75">
      <h1 className="text-center">Registration Form</h1>
      <form className="mt-4" onSubmit={onFinish}>
        <div className="mb-3">
          <label htmlFor="userName" className="form-label">UserName </label>
          <input type="text" className="form-control" id="userName" name="userName" ref={userName} required />
        </div>
        <div className="mb-3">
          <label htmlFor="phoneNo" maxLength={10} className="form-label">
            Phone No
          </label>
          <div className="input-group">
            <span className="input-group-text">+91</span>
            <input type="number tel" maxLength="10"className="form-control" id="phoneNo" name="phoneNo" ref={phoneNo} required />
          </div>
        </div>
        <div className="mb-3">
          <label htmlFor="email" className="form-label">
            E-mail
          </label>
          <input type="email" className="form-control" id="email" name="email" ref={email} required />
        </div>
        <div className="mb-3">
          <label htmlFor="password" className="form-label">
            Password
          </label>
          <input type="password" className="form-control" id="password" name="password" ref={password} required />
        </div>
        <div className="mb-3">
          <label htmlFor="confirmPassword" className="form-label">
            Confirm Password
          </label>
          <input
            type="password"
            className="form-control"
            id="confirmPassword"
            name="confirmPassword"
            ref={confirmPassword}
            required

          />
        </div>
        <div className="mb-3">
          <label htmlFor="personalAddress" className="form-label">
            Personal Address
          </label>
          <input
            type="text"
            className="form-control"
            id="personalAddress"
            name="personalAddress"
            ref={personalAddress}
            required
          />
        </div>
        <div className="row mb-3">
          <div className="col-md-3">
            <label htmlFor="state" className="form-label">
              State
            </label>
            <select className="form-control " id="State" name='State' ref={state} required>
              <option value="">Select a state</option>
              {states.map((state, index) => (
                <option key={index} value={state}>
                  {state}
                </option>
              ))}
            </select>
          </div>
          <div className="col-md-3">
            <label htmlFor="country" className="form-label">
              Country
            </label>
            <select className="form-control " id="country" name='country' ref={country} required>
              <option value="">Select a Country</option>
              {countries.map((countries, index) => (
                <option key={index} value={countries}>
                  {countries}
                </option>
              ))}
            </select>
            {/* <input type="text" className="form-control" id="country" name='country' ref={country} required /> */}
          </div>
          <div className="col-md-3">
            <label htmlFor="city" className="form-label">
              City
            </label>
            <input type="text" className="form-control" id="city" name="city" ref={city} required />
          </div>
          <div className="col-md-3">
            <label htmlFor="zipcode" className="form-label">
              ZipCode
            </label>
            <input type="text" className="form-control" id="zipcode" name="zipcode" ref={zipcode} required />
          </div>
        </div>
        <div className="mb-3">
          <label htmlFor="companyName" className="form-label">
            Company Name
          </label>
          <input type="text" className="form-control" id="companyName" name="companyName" ref={companyName} required />
        </div>
        <div className="mb-3">
          <label htmlFor="companyContactNo" className="form-label">
            Company Contact No
          </label>
          <div className="input-group">
            <span className="input-group-text">+91</span>
            <input
              type="text"
              className="form-control"
              id="companyContactNo"
              name="companyContactNo"
              ref={companyContactNo}
              required

            />
          </div>
        </div>
        <div className="mb-3">
          <label htmlFor="companyAddress" className="form-label">
            Company Address
          </label>
          <input
            type="text"
            className="form-control"
            id="companyAddress"
            name="companyAddress"
            ref={companyAddress}
            required

          />
        </div>
        <div className="row mb-3">
          <div className="col-md-3">
            <label htmlFor="companyCountry" className="form-label">
              Company State
            </label>
            <select className="form-control " id="companyState" name='companyState' ref={companyState} required>
              <option value="">Select a state</option>
              {states.map((state, index) => (
                <option key={index} value={state}>
                  {state}
                </option>
              ))}
            </select>
            {/* <input type="text" className="form-control" id="companyState" name="companyState"  ref={companyState} required/> */}
          </div>
          <div className="col-md-3">
            <label htmlFor="companyCountry" className="form-label">
              Company Country
            </label>
            <select className="form-control " id="companyState" name='companyState' ref={companyCountry} required>
              <option value="">Select a Country</option>
              {countries.map((countries, index) => (
                <option key={index} value={countries}>
                  {countries}
                </option>
              ))}
            </select>
            {/* <input type="text" className="form-control" id="companyCountry" name="companyCountry"  ref={companyCountry} required/> */}
          </div>
          <div className="col-md-3">
            <label htmlFor="companyCity" className="form-label">
              Company City
            </label>
            <input type="text" className="form-control" id="companyCity" name="companyCity" ref={companyCity} required />
          </div>
          <div className="col-md-3">
            <label htmlFor="companyZipcode" className="form-label">
              Company ZipCode
            </label>
            <input type="text" className="form-control" id="companyZipcode" name="companyZipcode" ref={companyZipcode} required />
          </div>
        </div>
        <div className="mb-3">
          <label htmlFor="gstn" className="form-label">
            GSTN
          </label>
          <input type="text" className="form-control" id="gstn" name="gstn" ref={gstn} required />
        </div>
        <div className="mb-3">
          <label htmlFor="businessLocation" className="form-label">
            Business Location
          </label>
          <input
            type="text"
            className="form-control"
            id="businessLocation"
            name="businessLocation"
            ref={businessLocation}
            required
          />
        </div>
        <div className="row mb-3">
        <div className="col-md-3">
            <label htmlFor="headbranch" className="form-label">
              Head Branch
            </label>
            <input type="text" className="form-control" id="headbranch" name="headbranch" ref={headbranch}  />
          </div>
          <div className="col-md-3">
            <label htmlFor="subbranch1" className="form-label">
              Sub Branch 1
            </label>
            <input type="text" className="form-control" id="subbranch1" name="subbranch1" ref={subbranch1}  />
          </div>
          <div className="col-md-3">
            <label htmlFor="subbranch2" className="form-label">
              Sub Branch2
            </label>
            <input type="text" className="form-control" id="subbranch2" name="subbranch2" ref={subbranch2} required />
          </div>
        </div>
        
        <div className="mb-3">
          <label htmlFor="businessCategory" className="form-label" >
            Business Category
          </label>
          <select className="form-select" id="businessCategory" ref={businessCategory} required>
            <option value="">Select Category</option>
            <option value="Infromation Technology">Infromation Technology</option>
            <option value="Fashion Industry">Fashion Industry</option>
            <option value="Interiors-Architecture">Interiors-Architecture</option>
            <option value="Health Care">Health Care</option>
            <option value="Eduvation & Training">Eduvation & Training</option>
            <option value="HR">HR</option>
            <option value="Clothing">Clothing</option>
            <option value="Water Treatment">Water Treatment</option>
            <option value="Vastu">Vastu</option>
            <option value="Civil Custruction">Civil Custruction</option>
            <option value="Skin Care & Beauty">Skin Care & Beauty</option>
            <option value="Entertainment">Entertainment</option>
            <option value="Events & Tourism">Events & Tourism</option>
          </select>
        </div>
        <div className="mb-3">
          <label htmlFor="businessType" className="form-label">
            Business Type
          </label>
          <select className="form-select" id="businessType" ref={businessType} required>
            <option value="">Select Type</option>
            <option value="Hardware-Sales">Hardware-Sales</option>
            <option value="Hardware-Service">Hardware-Service</option>
            <option value="Software- Sales">Software- Sales</option>
            <option value="Software- Service">Software- Service</option>
            <option value="Software-Training">Software-Training</option>
            <option value="Software Dveleopment">Software Dveleopment</option>
            <option value="Software-Architechture">Software-Architechture</option>
            <option value="Womens Fashion Designer">Womens Fashion Designer</option>
            <option value="Fashion Shows & Events">Fashion Shows & Events</option>
            <option value="Mens Fashin Designer">Mens Fashin Designer</option>
            <option value="Kinds Fashion Designer">Kinds Fashion Designer</option>
            <option value="Interior Decorators">Interior Decorators</option>
            <option value="Interior Suppliers">Interior Suppliers</option>
            <option value="Interior Architects">Interior Architects</option>
            <option value="Interior Designers">Interior Designers</option>
            <option value="Interiors Manufactures">Interiors Manufactures</option>
            <option value="Interiors Office Furnitures">Interiors Office Furnitures</option>
            <option value="Interiors Office Supplies">Interiors Office Supplies</option>
            <option value="Scicologist">Scicologist</option>
            <option value="Stiching">Stiching</option>
            <option value="Readymade Dress">Readymade Dress</option>
            <option value="Raki">Raki</option>
            <option value="Healing">Healing</option>
            <option value="Vastu Consultants">Vastu Consultants</option>
            <option value="Travel Guide">Travel Guide</option>
            <option value="Resorts">Resorts</option>
            <option value="Hotels">Hotels</option>
          </select>
        </div>
        <div className="mb-3">
          <label htmlFor="Planes" className="form-label">
            Plan
          </label>
          <select className="form-select" id="planes" ref={plan} required>
            <option value="">Select Type</option>
            <option value="86">GOLD</option>
            <option value="87">SILVER</option>
            <option value="87">PLATINUM</option>
            <option value="87">DIAMOND</option>
          </select>
        </div>
        <div className="mb-3">
          <label htmlFor="businessDescription" className="form-label" ref={businessDescription} required>
            Business Description
          </label>
          <textarea
            className="form-control"
            id="businessDescription"
            name="businessDescription"
            rows="3"

          ></textarea>
        </div>
        <div className="mb-3">
          <label htmlFor="image" className="form-label" >
            Upload Image</label>
          {/* <input type="file" className="form-control" id="image" name="image" ref={image} required /> */}
          <input type="file"  className="form-control" id="image" onChange={handleFileChange} name="images" multiple required />

        </div>
        <div className="mb-3">
          <label htmlFor="video" className="form-label">
            YouTube Video Link
          </label>
          <input type="url" className="form-control" id="video" name="video" ref={video} required />
        </div>
        {/* <iframe width="560" height="315" src="https://www.youtube.com/embed/AgOlyOEkNEY" title="YouTube video player" frameborder="0" allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture; web-share" allowfullscreen></iframe> */}
        <button type="submit" className="btn btn-primary" disabled={loading}>
          {loading ? 'Loading...' : 'Register'}
        </button>
      </form>
    </div>
  );
};

export default Registration;
