// export default Registration;




import React, { useState } from 'react';
import axios from 'axios';
import { UploadOutlined } from '@ant-design/icons';
import { Button, Form, Input, Select, Upload, Space } from 'antd';

const { Option } = Select;

const Registration = () => {
  const [form] = Form.useForm();
  const [loading, setLoading] = useState(false);

  const onFinish = async (values) => {
    setLoading(true);
    try {
      const response = await axios.post('/api/registration', values);
      console.log(response.data); // Data saved successfully
      form.resetFields();
    } catch (error) {
      console.error(error);
    } finally {
      setLoading(false);
    }
  };

  const prefixSelector = (
    <Form.Item name="prefix" noStyle>
      <Select style={{ width: 70 }}>
        <Option value="86">+91</Option>
        <Option value="87">+81</Option>
      </Select>
    </Form.Item>
  );

  const normFile = (e) => {
    console.log('Upload event:', e);
    if (Array.isArray(e)) {
      return e;
    }
    return e?.fileList;
  };

  const onWebsiteChange = (value) => {
    // Your logic here
  };

  return (
    <div className="" style={{ backgroundColor: '#f5f5f5' }}>
      <div className="d-flex justify-content-center"></div>
      <h1></h1>
      <Form
        method="POST"
        className="registration-form"
        id="registration-form"
        {...formItemLayout}
        form={form}
        onFinish={onFinish}
        style={{ maxWidth: 600 }}
        scrollToFirstError
      >
        {/* Rest of the form fields */}
        {/* ... */}
        {/* Upload */}
        <Form.Item
          name="UserName"
          label="UserName"
          onChange={onWebsiteChange}
          rules={[
            {
              required: true,
              message: 'Please input your UserName',
            },
          ]}
        >
          <Input />
        </Form.Item>
        <Form.Item
          name="PhoneNo"
          label="Phone No"
          rules={[
            {
              required: true,
              message: 'Please input your phone number!',
            },
          ]}
        >
          <Input
            addonBefore={prefixSelector}
            style={{
              width: '100%',
            }}
          />
        </Form.Item>
        <Form.Item
          name="email"
          label="E-mail"
          onChange={onWebsiteChange}
          rules={[
            {
              type: 'email',
              message: 'The input is not a valid E-mail!',
            },
            {
              required: true,
              message: 'Please input your E-mail!',
            },
          ]}
        >
          <Input />
        </Form.Item>
        <Form.Item
          name="password"
          label="Password"
          onChange={onWebsiteChange}
          rules={[
            {
              required: true,
              message: 'Please input your password!',
            },
          ]}
          hasFeedback
        >
          <Input.Password />
        </Form.Item>
        <Form.Item
          name="confirm"
          label="Confirm Password"
          onChange={onWebsiteChange}
          dependencies={['password']}
          hasFeedback
          rules={[
            {
              required: true,
              message: 'Please confirm your password!',
            },
            ({ getFieldValue }) => ({
              validator(_, value) {
                if (!value || getFieldValue('password') === value) {
                  return Promise.resolve();
                }
                return Promise.reject(new Error('The passwords do not match!'));
              },
            }),
          ]}
        >
          <Input.Password />
        </Form.Item>
        {/* Personal Address */}
        <Form.Item
          name="PersonalAddress"
          label="Personal Address"
          onChange={onWebsiteChange}
          rules={[
            {
              required: true,
              message: 'Please input your Personal Address',
            },
          ]}
        >
          <Input />
        </Form.Item>
        <Form.Item label="State">
          <Space compact>
            <Form.Item
              name={['State', 'Country', 'province']}
              noStyle
              rules={[
                {
                  required: true,
                  message: 'Province is required',
                },
              ]}
            >
              <Select placeholder="State">
                <Option value="Karnataka">Karnataka</Option>
                <Option value="Jiangsu">Jiangsu</Option>
              </Select>
            </Form.Item>
            <Form.Item
              name={['State', 'province']}
              noStyle
              rules={[{ message: 'Province is required' }]}
            >
              <Select placeholder="Country">
                <Option value="Indian">Indian</Option>
                <Option value="Jiangsu">Jiangsu</Option>
              </Select>
            </Form.Item>
            <Form.Item
              name={['State', 'Zipcode']}
              noStyle
              rules={[
                {
                  required: true,
                  message: 'Zipcode is required',
                },
              ]}
            >
              <Input style={{ width: '60%' }} placeholder="ZipCode" />
            </Form.Item>
          </Space>
        </Form.Item>
        {/* Company Name */}
        <Form.Item
          name="CompanyName"
          label="Company Name"
          onChange={onWebsiteChange}
          rules={[
            {
              required: true,
              message: 'Please input your Company name',
            },
          ]}
        >
          <Input />
        </Form.Item>
        <Form.Item
          name="CompanyContactNo"
          label="Company Contact No"
          onChange={onWebsiteChange}
          rules={[
            {
              required: true,
              message: 'Please input your phone number!',
            },
          ]}
        >
          <Input
            addonBefore={prefixSelector}
            style={{
              width: '100%',
            }}
          />
        </Form.Item>
        {/* Company Address */}
        <Form.Item
          name="CompanyAddress"
          label="Company Address"
          onChange={onWebsiteChange}
          rules={[
            {
              required: true,
              message: 'Please input your Company Address',
            },
          ]}
        >
          <Input />
        </Form.Item>
        <Form.Item label="State">
          <Space compact>
            <Form.Item
              name={['CompanyState', 'CompanyCountry', 'province']}
              noStyle
              rules={[
                {
                  required: true,
                  message: 'Province is required',
                },
              ]}
            >
              <Select placeholder="State">
                <Option value="Karnataka">Karnataka</Option>
                <Option value="Jiangsu">Jiangsu</Option>
              </Select>
            </Form.Item>
            <Form.Item
              name={['CompanyState', 'CompanyCountry', 'province']}
              noStyle
              rules={[{ message: 'Province is required' }]}
            >
              <Select placeholder="Country">
                <Option value="Indian">Indian</Option>
                <Option value="Jiangsu">Jiangsu</Option>
              </Select>
            </Form.Item>
            <Form.Item
              name={['CompanyState', 'Country', 'Zipcode']}
              noStyle
              rules={[
                {
                  required: true,
                  message: 'Zipcode is required',
                },
              ]}
            >
              <Input style={{ width: '60%' }} placeholder="ZipCode" />
            </Form.Item>
          </Space>
        </Form.Item>
        {/* GSTN */}
        <Form.Item
          name="GSTN"
          label="GSTN"
          onChange={onWebsiteChange}
          placeholder="NA"
          rules={[
            {
              type: 'tel',
              message: 'The input is not a valid GSTN',
            },
            {
              required: true,
              message: 'Please input your GSTN',
            },
          ]}
        >
          <Input />
        </Form.Item>
        {/* Business Location */}
        <Form.Item
          name="BusinessLocation"
          label="Business Location"
          onChange={onWebsiteChange}
          rules={[
            {
              type: 'Business Location',
              message: 'The input is not a valid Business Location',
            },
            {
              required: true,
              message: 'Please input your Business Location',
            },
          ]}
        >
          <Input />
        </Form.Item>
        <Form.Item
          name="Business Category"
          label="Business Category"
          onChange={onWebsiteChange}
          rules={[
            {
              type: 'Business Category',
              message: 'The input is not a valid Business Category',
            },
            {
              required: true,
              message: 'Please input your Business Category',
            },
          ]}
        >
          <Select>
            <Option value="86">+91</Option>
            <Option value="87">+81</Option>
          </Select>
        </Form.Item>
        <Form.Item
          name="Business Type"
          label="Business Type"
          onChange={onWebsiteChange}
          rules={[
            {
              type: 'Business Type',
              message: 'The input is not a valid Business Type',
            },
            {
              required: true,
              message: 'Please input your Business Type',
            },
          ]}
        >
          <Select>
            <Option value="86">+91</Option>
            <Option value="87">+81</Option>
          </Select>
        </Form.Item>
        {/* Business Description */}
        <Form.Item
          name="BusinessDescription"
          label="Business Description"
          onChange={onWebsiteChange}
          rules={[
            {
              required: true,
              message: 'Please input your Business Description',
            },
          ]}
        >
          <Input.TextArea showCount maxLength={500} />
        </Form.Item>
        <Form.Item
          name="upload"
          label="Upload"
          valuePropName="fileList"
          getValueFromEvent={normFile}
        >
          <Upload
            name="logo"
            action="https://www.mocky.io/v2/5cc8019d300000980a055e76"
            listType="picture"
            maxCount={5}
            multiple
          >
            <Button icon={<UploadOutlined />}>Click to upload.jpg image(Max: 5)</Button>
          </Upload>
        </Form.Item>
        <Form.Item
          name="upload"
          label="Upload"
          valuePropName="fileList"
          getValueFromEvent={normFile}
        >
          <Upload
            name="logo"
            action="https://www.mocky.io/v2/5cc8019d300000980a055e76"
            listType="videos"
            maxCount={2}
            multiple
          >
            <Button icon={<UploadOutlined />}>Click to upload.mp4 file(Max: 2)</Button>
          </Upload>
        </Form.Item>
        <Form.Item {...tailFormItemLayout}>
          <Button type="primary" htmlType="submit" loading={loading}>
            Register
          </Button>
        </Form.Item>
      </Form>
    </div>
  );
};

export default Registration;
